import React,{Component} from 'react';
import { Platform, StyleSheet, Text, View, TouchableOpacity } from 'react-native';

export default class App extends Component{

  constructor(props){
    super(props)
    this.state = {
      cronometro:null,
      number: 0,
      startStopText:'Start',
    }
    this.startStopButton = this.startStopButton.bind(this);
    this.clearButton = this.clearButton.bind(this);
    this.pauseButton = this.pauseButton.bind(this);
  }

  startStopButton(){
    //começa ou para o cronometro

    if(this.state.cronometro == null){
      //inicia o cronometro
      let newS = this.state;
      newS.startStopText = 'Stop';
      this.setState(newS);

      this.state.cronometro= setInterval(()=>{
        let newState = this.state;
        newState.number +=0.1;
        this.setState(newState);
      },100);
    }
  }

  clearButton(){
    clearInterval(this.state.cronometro);
    let newState = this.state;
    newState.startStopText = 'Start';
    newState.cronometro = null;
    newState.number = 0;
    this.setState(newState);
  }

  pauseButton(){
    if(this.state.cronometro != null){
      clearInterval(this.state.cronometro);
      let newState = this.state;
      newState.startStopText = 'Start';
      newState.cronometro = null;
      this.setState(newState);
    }
  }

  render(){
  return (
    <View style={styles.body}>

      <View  style={styles.tContainer}>
        <View style={styles.timer}>
          <Text  style={styles.counterText}>{this.state.number.toFixed(1)}</Text>
        </View>
        <View style={styles.containerBotao}>
          <TouchableOpacity style={styles.botao} onPress={this.startStopButton}>
            <Text style={styles.botaoText}>{this.state.startStopText}</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.botao} onPress={this.pauseButton}>
            <Text style={styles.botaoText}>pause</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.botao} onPress={this.clearButton}>
            <Text style={styles.botaoText}>clear</Text>
          </TouchableOpacity>
        </View>

      </View>
    </View>
  );
  }
}

const styles = StyleSheet.create({
  body: {
    flex: 1,
    backgroundColor: '#221D41',
    justifyContent: 'center',
  },

  tContainer:{
    justifyContent: 'center',
    alignItems:'center'
  },

  counterText:{
    color:'#221D41',
    fontSize:40,
    fontWeight:'bold',
  },

  timer:{
    width:'20%',
    height:'100%',
    backgroundColor:'#fff',
    borderRadius:70,
    justifyContent:'center',
    alignItems:'center',

  },

  containerBotao:{
    flexDirection:'row',
    padding:20,
  },

  botao:{
    backgroundColor:'#331DF4',
    marginHorizontal: 15,
    borderRadius:10,
    height:40,
    width:70,
    justifyContent:'center',
    alignItems:'center',
  },

  botaoText:{
    color:'#fff',
    fontWeight:'bold',


  }

});

